import React, { useState, useEffect } from "react";
import CollegeDataService from "../services/college";
import { Link } from "react-router-dom";

const CollegesList = props => {
  const [colleges, setColleges] = useState([]);
  const [searchName, setSearchName ] = useState("");
  const [searchID, setSearchID ] = useState("");
  const [searchCourse, setSearchCourse] = useState("");
  const [courses, setCourses] = useState(["All Courses"]);

  useEffect(() => {
    retrieveColleges();
    retrieveCourses();
  }, []);

  const onChangeSearchName = e => {
    const searchName = e.target.value;
    setSearchName(searchName);
  };

  const onChangeSearchID= e => {
    const searchID = e.target.value;
    setSearchID(searchID);
  };

  const onChangeSearchCourse = e => {
    const searchCourse = e.target.value;
    setSearchCourse(searchCourse);

  };

  const retrieveColleges = () => {
    CollegeDataService.getAll()
      .then(response => {
        console.log(response.data);
        setColleges(response.data.colleges);

      })
      .catch(e => {
        console.log(e);
      });
  };

  const retrieveCourses = () => {
    CollegeDataService.getCourses()
      .then(response => {
        console.log(response.data);
        setCourses(["All Courses"].concat(response.data));

      })
      .catch(e => {
        console.log(e);
      });
  };

  const refreshList = () => {
    retrieveColleges();
  };

  const find = (query, by) => {
    CollegeDataService.find(query, by)
      .then(response => {
        console.log(response.data);
        setColleges(response.data.colleges);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const findByName = () => {
    find(searchName, "name")
  };

  const findByID = () => {
    find(searchID, "ID")
  };

  const findByCourse = () => {
    if (searchCourse == "All Courses") {
      refreshList();
    } else {
      find(searchCourse, "course")
    }
  };

  return (
    <div>
      <div className="row pb-1">
        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name"
            value={searchName}
            onChange={onChangeSearchName}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByName}
            >
              Search
            </button>
          </div>
        </div>
        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by ID"
            value={searchID}
            onChange={onChangeSearchID}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByID}
            >
              Search
            </button>
          </div>
        </div>
        <div className="input-group col-lg-4">

          <select onChange={onChangeSearchCourse}>
             {courses.map(course => {
               return (
                 <option value={course}> {course.substr(0, 20)} </option>
               )
             })}
          </select>
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByCourse}
            >
              Search
            </button>
          </div>

        </div>
      </div>
      <div className="row">
        {colleges.map((college) => {
          const address = `${college.City} ${college.State}, ${college.Country}`;
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{college.Name}</h5>
                  <p className="card-text">
                    <strong>Courses: </strong>{college.Courses}<br/>
                    <strong>Address: </strong>{address}
                    <strong>College type: </strong>{college.Sector}
                  </p>
                  <div className="row">
                  <Link to={"/Colleges/"+college._id} className="btn btn-primary col-lg-5 mx-1 mb-1">
                    View Students
                  </Link>
                  <a target="_blank" href={"https://www.google.com/maps/place/" + address} className="btn btn-primary col-lg-5 mx-1 mb-1">View Map</a>
                  </div>
                </div>
              </div>
            </div>
          );
        })}


      </div>
    </div>
  );
};

export default CollegesList;
