import React from "react";
import { Switch, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import College from "./components/colleges";
import CollegesList from "./components/colleges-list";
// import Login from "./components/login";

import Container from "react-bootstrap/Container";
import Navbar from "react-bootstrap/Navbar";
import Session from "/components/Session";
import Pagination from "/components/Pagination";
import GoTopButton from "/components/GoTopButton";
import SessionSelect from "/components/SessionSelect";
import { useScroll } from "/helpers/scroll";


function App() {

  const allSessionsCount = 100;
  const [sessionsPerPage, setSessionsPerPage] = useState(20);
  const [currentPage, setCurrentPage] = useState(1);
  const scrollPosition = useScroll();

  const lastSessionNumber = currentPage * sessionsPerPage;
  const firstSessionIndex = lastSessionNumber - sessionsPerPage;



  return (
    <div>
      <nav className="navbar navbar-expand navbar-dark bg-dark">
        <a href="/colleges" className="navbar-brand" >
          Top 100 Engineering Colleges In India
        </a>
        {/* <div className="navbar-nav mr-auto">
          <li className="nav-item">
            <Link to={"/colleges"} className="nav-link">
              Colleges
            </Link>
          </li>

        </div> */}
      </nav>

      <div className="container mt-3">
        <Switch>
          <Route exact path={["/", "/colleges"]} component={CollegesList} />
          <Route 
            path="/colleges/:id"
            render={(props) => (
              <College {...props}  />
            )}
          />
        </Switch>
      </div>

      <Navbar bg="dark" variant="dark">
        <Navbar.Brand className="navbar">
          <div>Loaded sessions: {allSessionsCount}</div>
          <SessionSelect
            itemsPerPage={sessionsPerPage}
            setItemsPerPage={setSessionsPerPage}
          />
        </Navbar.Brand>
      </Navbar>
      <Container>
        <Pagination
          itemsCount={allSessionsCount}
          itemsPerPage={sessionsPerPage}
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
          alwaysShown={false}
        />
        <GoTopButton visible={scrollPosition > 400} />
      </Container>

    </div>





  );
}

export default App;
